-------------------------------------- Urheberrecht --------------- by [LA]Leyynen ------------------- 2012 ---------------------------------------------- Script by Noneatme ------addEvent("doPlayerPlaceObject", true)-- VARIABLEN --local sFunc = {}local sSetting = {}local pObject = {}sSetting["lastid"] = 1-- FUNCTIONS --sFunc["placeObjectHandler"] = function(id, x, y, z, rotx, roty, rotz)	local data	local name, mysqlitem	if(id == 1281) then -- Tisch		data = getPlayerWeihnachtsData(source, "wn:tische")		name = "wn:tische"		mysqlitem = "TISCHE"	elseif(id == 1215) then -- Licht		data = getPlayerWeihnachtsData(source, "wn:lichter")		name = "wn:lichter"		mysqlitem = "LICHTER"	elseif(id == 650) or (id == 651) or (id == 653) then -- Cacti		data = getPlayerWeihnachtsData(source, "wn:kakteen")		name = "wn:kakteen"		mysqlitem = "KAKTEEN"	elseif(id == 882) then -- Oh Tannenbaum oh Tannenbaum		data = getPlayerWeihnachtsData(source, "wn:tannenbaeume")		name = "wn:tannenbaeume"		mysqlitem = "TANNENBAEUME"	elseif(id == 628) then -- Oh Weihnachtspalme oh Weihnachtspalme		data = getPlayerWeihnachtsData(source, "wn:palmen")		name = "wn:palmen"		mysqlitem = "PALMEN"	end	data = tonumber(data)	if not(data) or (data < 1) then		return;	end	givePlayerWeinachtsData(source, name, mysqlitem, -1, nil) 	if(id) and (x) and (y) and (z) and (rotx) and (roty) and (rotz) then		pObject[sSetting["lastid"]] = createObject(id, x, y, z, rotx, roty, rotz)		sSetting["lastid"] = sSetting["lastid"]+1	endendsFunc["deleteAllPlacedObjects"] = function()	for index, objekt in pairs(pObject) do		destroyElement(objekt)	endend--- EVENT HANDLERS --addEventHandler("doPlayerPlaceObject", getRootElement(), sFunc["placeObjectHandler"])