-------------------------------------- Urheberrecht --------------- by [LA]Leyynen ------------------- 2012 ---------------------------------------------- Script by Noneatme -------- VARIABLEN --local cFunc = {}local cSetting = {}cSetting["guiEnabled"] = false	local Static = {	label = {},	button = {},	staticimage = {},	window = {},}local preise = {	["Geld"] = 100,	["Objekte"] = 10,	["Gehstock"] = 5,	["Feuerwerk"] = 10,	["Turismo"] = 1000,	["Mr.Whoopee"] = 500,	["Hausmarker"] = 2500,	["Adminposten"] = 9999999}	-- FUNKTIONEN --cFunc["buildPackageGui"] = function()	if(cSetting["guiEnabled"] == true) then		return	end	cSetting["guiEnabled"] = true	showCursor(true)	setElementData ( getLocalPlayer(), "ElementClicked", true )		local X, Y, Width, Height = getMiddleGuiPosition(417, 255)	Static.window[1] = guiCreateWindow(X, Y, Width, Height, "Päckchen", false)	guiWindowSetSizable(Static.window[1], false)	guiSetAlpha(Static.window[1], 1.0)	Static.staticimage[1] = guiCreateStaticImage(9, 22, 117, 66, "images/weihnachten/paeckchen.jpg", false, Static.window[1])	Static.label[1] = guiCreateLabel(2, 0, 115, 66, "Deine Päckchen:\n"..getElementData(localPlayer, "wn:paeckchen"), false, Static.staticimage[1])	guiSetFont(Static.label[1], "default-bold-small")	guiLabelSetHorizontalAlign(Static.label[1], "center", false)	guiLabelSetVerticalAlign(Static.label[1], "center")	guiLabelSetColor(Static.label[1], 0, 255, 255)	Static.label[2] = guiCreateLabel(131, 22, 276, 66, "Päckchen bekommst du jedes Jahr\nam Weihnachtsevent.\nWas du dir damit kaufen kannst, kannst du hier\nsehen! (Alle Kosten in Päckchen.)", false, Static.window[1])	guiSetFont(Static.label[2], "default-bold-small")	Static.label[3] = guiCreateLabel(4, 85, 407, 15, "_________________________________________________________________________________", false, Static.window[1])	Static.button[1] = guiCreateButton(168, 210, 95, 34, "Schliessen", false, Static.window[1])	guiSetFont(Static.button[1], "default-bold-small")	guiSetProperty(Static.button[1], "NormalTextColour", "FFAAAAAA")	Static.button[2] = guiCreateButton(11, 110, 95, 34, "10.000$\nKosten: "..preise["Geld"], false, Static.window[1])	guiSetFont(Static.button[2], "default-bold-small")	guiSetProperty(Static.button[2], "NormalTextColour", "FFAAAAAA")	Static.button[3] = guiCreateButton(11, 158, 95, 34, "Platz. Objekte\nKosten: "..preise["Objekte"], false, Static.window[1])	guiSetFont(Static.button[3], "default-bold-small")	guiSetProperty(Static.button[3], "NormalTextColour", "FFAAAAAA")	Static.button[4] = guiCreateButton(109, 110, 95, 34, "Gehstock\nKosten: "..preise["Gehstock"], false, Static.window[1])	guiSetFont(Static.button[4], "default-bold-small")	guiSetProperty(Static.button[4], "NormalTextColour", "FFAAAAAA")	Static.button[5] = guiCreateButton(108, 159, 95, 34, "Feuerwerk\nKosten: "..preise["Feuerwerk"], false, Static.window[1])	guiSetFont(Static.button[5], "default-bold-small")	guiSetProperty(Static.button[5], "NormalTextColour", "FFAAAAAA")	Static.button[6] = guiCreateButton(207, 110, 95, 34, "Turismo\nKosten: "..preise["Turismo"], false, Static.window[1])	guiSetFont(Static.button[6], "default-bold-small")	guiSetProperty(Static.button[6], "NormalTextColour", "FFAAAAAA")	Static.button[7] = guiCreateButton(207, 160, 95, 34, "Mr.Whoopee\nKosten: "..preise["Mr.Whoopee"], false, Static.window[1])	guiSetFont(Static.button[7], "default-bold-small")	guiSetProperty(Static.button[7], "NormalTextColour", "FFAAAAAA")	Static.button[8] = guiCreateButton(307, 160, 95, 34, "Hausmarker n.W\nKosten: "..preise["Hausmarker"], false, Static.window[1])	guiSetFont(Static.button[8], "default-bold-small")	guiSetProperty(Static.button[8], "NormalTextColour", "FFAAAAAA")	Static.button[9] = guiCreateButton(307, 110, 95, 34, "Adminposten\nKosten:"..preise["Adminposten"], false, Static.window[1])	guiSetFont(Static.button[9], "default-bold-small")	guiSetProperty(Static.button[9], "NormalTextColour", "FFAAAAAA")		-- EVENT HANDLERS --	addEventHandler("onClientGUIClick", Static.button[2], function()		triggerServerEvent("doPaekchenBuy", localPlayer, "Geld")		destroyElement(Static.window[1])		cSetting["guiEnabled"] = false		showCursor(false)		setElementData ( getLocalPlayer(), "ElementClicked", false )	end, false)		addEventHandler("onClientGUIClick", Static.button[3], function()		triggerServerEvent("doPaekchenBuy", localPlayer, "Objekte")		destroyElement(Static.window[1])		cSetting["guiEnabled"] = false		showCursor(false)		setElementData ( getLocalPlayer(), "ElementClicked", false )	end, false)		addEventHandler("onClientGUIClick", Static.button[4], function()		triggerServerEvent("doPaekchenBuy", localPlayer, "Gehstock")		destroyElement(Static.window[1])		cSetting["guiEnabled"] = false		showCursor(false)		setElementData ( getLocalPlayer(), "ElementClicked", false )	end, false)		addEventHandler("onClientGUIClick", Static.button[5], function()		triggerServerEvent("doPaekchenBuy", localPlayer, "Feuerwerk")		destroyElement(Static.window[1])		cSetting["guiEnabled"] = false		showCursor(false)		setElementData ( getLocalPlayer(), "ElementClicked", false )	end, false)		addEventHandler("onClientGUIClick", Static.button[6], function()		triggerServerEvent("doPaekchenBuy", localPlayer, "Turismo")		destroyElement(Static.window[1])		cSetting["guiEnabled"] = false		showCursor(false)		setElementData ( getLocalPlayer(), "ElementClicked", false )	end, false)		addEventHandler("onClientGUIClick", Static.button[7], function()		triggerServerEvent("doPaekchenBuy", localPlayer, "Mr.Whoopee")		destroyElement(Static.window[1])		cSetting["guiEnabled"] = false		showCursor(false)		setElementData ( getLocalPlayer(), "ElementClicked", false )	end, false)		addEventHandler("onClientGUIClick", Static.button[8], function()		triggerServerEvent("doPaekchenBuy", localPlayer, "Hausmarker")		destroyElement(Static.window[1])		cSetting["guiEnabled"] = false		showCursor(false)		setElementData ( getLocalPlayer(), "ElementClicked", false )	end, false)		addEventHandler("onClientGUIClick", Static.button[9], function()		triggerServerEvent("doPaekchenBuy", localPlayer, "Adminposten")		destroyElement(Static.window[1])		cSetting["guiEnabled"] = false		showCursor(false)		setElementData ( getLocalPlayer(), "ElementClicked", false )	end, false)		addEventHandler("onClientGUIClick", Static.button[1], function()		destroyElement(Static.window[1])		cSetting["guiEnabled"] = false		showCursor(false)		setElementData ( getLocalPlayer(), "ElementClicked", false )	end, false)endaddCommandHandler("paeckchen", function()	if(cSetting["guiEnabled"] == true) then		destroyElement(Static.window[1])		cSetting["guiEnabled"] = false		showCursor(false)		setElementData ( getLocalPlayer(), "ElementClicked", false )	else		cFunc["buildPackageGui"]()	endend)